con:config.script:
//reads in the next line of data from the csv file

def TitleFieldsList = context.expand( '${#TestCase#titleList}' )//read in the title list from the properties
String[] titleFields = TitleFieldsList.split(",") // split the titleFieldsList into an array

def titleCount = titleFields.size() //count the number of titles

// remove any existing properties
for( prop in context.testCase.properties) 
{
   context.testCase.removeProperty( prop.value.name )
}


//We can use the same fileReader as the Setup script because it was assigned to the context variable.
nextLine = context.fileReader.readLine()

if(nextLine != null) //if next line of data isn't null then read it in
{
 testRunner.testCase.setPropertyValue("titleList",TitleFieldsList) //add the TitleFieldList back to the properties
 String[] propData = nextLine.split(",") // read in the next set of values from the csv
 for(z=0; z<=titleCount-1; z++) // for each title create a property with the title and value from the CSV
 {
  testRunner.testCase.setPropertyValue(titleFields[z],propData[z])
  //log.info "count "+z +" "+titleFields[z]+" "+propData[z]
 }
  testRunner.gotoStep(0) // proceed to the next relevant step
}

con:testCase.con:setupScript:
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectPath = groovyUtils.projectPath //gets the path of the project root

//Create a new filereader object, using the context variable so it can be used between test components
context.fileReader = new BufferedReader(new FileReader(projectPath+"\\HeroList.csv"))
def curTC = testCase

//Read in the first line of the data file
firstLine = context.fileReader.readLine()

//The first line is the titles. We want to use this to name the properties we are going to set up.
//Set the firstline as a property itself so we can use this in ReadNextLine Groovy Script
curTC.setPropertyValue("titleList",firstLine)

//Split the first line into a string array
String[] titleFields = firstLine.split(",")

//Read in the next line and split it into a string array
firstLine = context.fileReader.readLine()
String[] propData = firstLine.split(",")


def titleCount = titleFields.size() // count how many titles there are
for(z=0; z<=titleCount-1; z++) // for each title add to the properties the title and the value
{
 curTC.setPropertyValue(titleFields[z],propData[z])
 //log.info "count "+z +" "+titleFields[z]+" "+propData[z]
}

con:mockOperation.con:dispatchPath:
log.info "Mock for GetHeroRealName has been triggered"
def xmlObj=new XmlSlurper().parseText(mockRequest.requestContent) def alias def first def last def heroName = xmlObj.Body.GetHeroRealName.alias log.info xmlObj.Body.GetHeroRealName.alias log.info heroName.toString().toUpperCase()
switch(heroName.toString().toUpperCase()) { case "SUPERMAN": alias = "Clark Kent" first = "Clark" last = "Kent" break case "SPIDERMAN": alias = "Peter Parker" first = "Peter" last = "Parker" break case "BATMAN": alias = "Bruce Wayne" first = "Bruce" last = "Wayne" break case "CAPTAIN AMERICA": alias = "Steven Rogers" first = "Steven" last = "Rogers" break case "DAREDEVIL": alias = "Matthew Michael Murdock" first = "Matthew" last = "Murdock" break case "INCREDIBLE HULK": alias = "Robert Bruce Banner" first = "Robert" last = "Banner" break case "IRON MAN": alias = "Tony Stark" first = "Tony" last = "Stark" break case "FLASH": alias = "Barry Allen" first = "Barry" last = "Allen" break case "WONDER WOMAN": alias = "Diana Prince" first = "Diana" last = "Prince" break default: log.info "No Alias Found For Hero "+heroName+". Returning Default" return "Default" break
}
context.alias = alias context.first = first context.last = last
log.info "Returning Mock Response For Here "+heroName return "SuccessfulResponse"
